import React from 'react'

const Details = ({ item, onClose }) => {
  return (
    <div className="details-modal">
      <h2>{item.title}</h2>
      <img src={item.img} alt={item.title} />
      <p>Price: {item.price}</p>
      <button onClick={onClose}>Cerrar</button>
    </div>
  );
};

export default Details;
