import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const Carousel = () => {
  const [counter, setCounter] = useState(0);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    afterChange: (current) => setCounter(current),
  };

  return (
    <div className="supercontainer">
      <h2>Carrusel de Imágenes</h2>
      <Slider {...settings}>
        <div className="slider-section">
          <img src="../SKINS/arma11.png" alt="Image 1" width="450" height="450" />
        </div>
        <div className="slider-section">
          <img src="../SKINS/arma15.png" alt="Image 2" width="450" height="450" />
        </div>
        <div className="slider-section">
          <img src="../SKINS/skin18.png" alt="Image 3" width="450" height="450" />
        </div>
        <div className="slider-section">
          <img src="../SKINS/arma2.png" alt="Image 4" width="450" height="450" />
        </div>
      </Slider>
      <div className="btn-left" onClick={() => setCounter((prev) => (prev <= 0 ? 3 : prev - 1))}>
        <i className="bx bx-chevron-left"></i>
      </div>
      <div className="btn-right" onClick={() => setCounter((prev) => (prev >= 3 ? 0 : prev + 1))}>
        <i className="bx bx-chevron-right"></i>
      </div>
    </div>
  );
};

export default Carousel;
