import React, { useContext } from 'react';
import { ThemeContext } from '../contexts/ThemeContext';
import themeIcon from '../IMAGES/sol-luna.png'; // Asegúrate de que la ruta sea correcta

const ThemeToggle = () => {
  const { toggleTheme } = useContext(ThemeContext);

  return (
    <button onClick={toggleTheme} style={{ background: 'none', border: 'none', cursor: 'pointer' }}>
      <img src={themeIcon} alt="Cambiar tema" style={{ width: '80px', height: '80px' }} />
    </button>
  );
};

export default ThemeToggle;
