import React, { useEffect, useRef, useState } from 'react';

const Carousel = () => {
  const [counter, setCounter] = useState(0);
  const sliderRef = useRef(null);
  const widthImg = 100 / 4; // 4 imágenes

  const moveToRight = () => {
    setCounter((prevCounter) => (prevCounter >= 3 ? 0 : prevCounter + 1));
  };

  const moveToLeft = () => {
    setCounter((prevCounter) => (prevCounter <= 0 ? 3 : prevCounter - 1));
  };

  useEffect(() => {
    const slider = sliderRef.current;
    if (slider) {
      slider.style.transform = `translate(-${counter * widthImg}%)`;
      slider.style.transition = 'all ease .6s';
    }

    const interval = setInterval(moveToRight, 3000);
    return () => clearInterval(interval); // Limpiar el intervalo al desmontar
  }, [counter]);

  return (
      <><div class="supercontainer">
          <div class="container-carousel">
              <div class="carruseles" id="slider">
                  <section class="slider-section">
                      <img src="SKINS/arma11.png" alt="Image 1" width="450" height="450" />
                  </section>
                  <section class="slider-section">
                      <img src="SKINS/arma15.png" alt="Image 2" width="450" height="450" />
                  </section>
                  <section class="slider-section">
                      <img src="SKINS/skin18.png" alt="Image 3" width="450" height="450" />
                  </section>
                  <section class="slider-section">
                      <img src="SKINS/arma2.png" alt="Image 4" width="450" height="450" />
                  </section>
              </div>
              <div class="btn-left"><i class="bx bx-chevron-left"></i></div>
              <div class="btn-right"><i class="bx bx-chevron-right"></i></div>
          </div>
      </div>
      
      <div className="carousel-container">
              <button className="btn-left" onClick={moveToLeft}>←</button>
              <div className="container-carousel" ref={sliderRef}>
                  <section className="slider-section"><img src="SKINS/arma11.png" alt="Image 1" width="450" height="450" /></section>
                  <section className="slider-section"><img src="SKINS/arma15.png" alt="Image 2" width="450" height="450" /></section>
                  <section className="slider-section"><img src="SKINS/skin18.png" alt="Image 3" width="450" height="450" /></section>
                  <section className="slider-section"><img src="SKINS/arma2.png" alt="Image 4" width="450" height="450" /></section>
              </div>
              <button className="btn-right" onClick={moveToRight}>→</button>
          </div></>
  );
};

export default Carousel;
