import React from "react";
import "../style.css";

const Details = ({ item, onClose }) => {
  const handleBuyClick = () => {
    // Verifica si el precio es un número
    if (typeof item.price === "string" && item.price.includes("$")) {
      alert("Artículo agregado al carrito.");
      // agregar la lógica para agregar al carrito
      onClose();
    } else {
      window.open(item.link, "_blank"); // Abre el enlace en una nueva pestaña
    }
  };

  return (
    <div className="details">
      <h2>{item.title}</h2>
      <img src={item.img} alt={item.title} className="details-image" />
      {item.originalPrice && item.discount && (
        <p className="details-discounted-price" style={{ textDecoration: 'line-through' }}>
          Precio original: <span>{item.originalPrice}</span> 
          <br />
          Precio con descuento: {item.price}
        </p>
      )}

      <div id="buttons">
        <button id="close-details" onClick={onClose}>
          Cerrar
        </button>
        <button id="buy" onClick={handleBuyClick}>
          {item.price && item.price.includes("$") ? "Agregar al carrito" : "Ver en Steam"}
        </button>
      </div>
    </div>
  );
};

export default Details;
