/* // Details.js
import React from 'react';

const Details = ({ item, onClose }) => {
  const handleBuyClick = () => {
    if (item.priceInt > 100) {
      alert("Artículo agregado al carrito.");
      // Aquí puedes agregar la lógica para agregar al carrito
      onClose();
    } else {
      window.open(item.link, "_blank"); // Abre el enlace en una nueva pestaña
    }
  };

  return (
    <div id="details" className="details">
      <img id="details-image" src={item.img} alt="Item Image" className="details-image" />
      <h2 id="details-title">{item.title}</h2>
      <p id="details-price">{item.price}</p>
      <div id="buttons">
        <button id="close-details" onClick={onClose}>Cerrar</button>
        <button id="buy" onClick={handleBuyClick}>
          {item.priceInt > 100 ? "Agregar al carrito" : "Comprar en Steam"}
        </button>
      </div>
    </div>
  );
};

export default Details;
 */
