import React, { useEffect, useRef, useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const Carousel = () => {
  const [counter, setCounter] = useState(0);
  const sliderRef = useRef(null);
  const widthImg = 100 / 4; // 4 im치genes

  const moveToRight = () => {
    setCounter((prevCounter) => (prevCounter >= 3 ? 0 : prevCounter + 1));
  };

  const moveToLeft = () => {
    setCounter((prevCounter) => (prevCounter <= 0 ? 3 : prevCounter - 1));
  };

  useEffect(() => {
    const slider = sliderRef.current;
    if (slider) {
      slider.style.transform = `translate(-${counter * widthImg}%)`;
      slider.style.transition = 'all ease .6s';
    }

    const interval = setInterval(moveToRight, 3000);
    return () => clearInterval(interval); // Limpiar el intervalo al desmontar
  }, [counter]);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    afterChange: (current) => setCounter(current),
  };

  return (
    <div className="supercontainer">
      <h2>Carrusel de Im치genes</h2>
      <Slider {...settings} ref={sliderRef}>
        <div className="slider-section">
          <img src="../SKINS/arma11.png" alt="Image 1" width="450" height="450" />
        </div>
        <div className="slider-section">
          <img src="../SKINS/arma15.png" alt="Image 2" width="450" height="450" />
        </div>
        <div className="slider-section">
          <img src="../SKINS/skin18.png" alt="Image 3" width="450" height="450" />
        </div>
        <div className="slider-section">
          <img src="../SKINS/arma2.png" alt="Image 4" width="450" height="450" />
        </div>
      </Slider>
      <div className="btn-left" onClick={moveToLeft}>
        <i className="bx bx-chevron-left"></i>
      </div>
      <div className="btn-right" onClick={moveToRight}>
        <i className="bx bx-chevron-right"></i>
      </div>
    </div>
  );
};

export default Carousel;
